mapper.update:
UPDATE review


        review_rate = #{reviewRate},


        review_content = #{reviewContent},

    review_date = CURRENT_TIMESTAMP,

        review_image = #{reviewImage}


WHERE review_id = #{reviewId}

mapper.select:
SELECT r.review_id AS reviewId, r.review_member_id AS reviewMemberId, r.review_receipt_id AS reviewReceiptId, r.review_image AS reviewImage, r.review_rate AS reviewRate, r.review_content AS reviewContent, r.review_date AS reviewDate, m.member_nickname AS memberNickName, s.shop_name AS shopName, GROUP_CONCAT(DISTINCT k.keyword SEPARATOR ',') AS reviewKeyword FROM review r JOIN receipt rc ON r.review_receipt_id = rc.receipt_id JOIN shop s ON rc.receipt_shop_id = s.shop_id JOIN member m ON r.review_member_id = m.member_id LEFT JOIN review_keyword rk ON r.review_id = rk.review_id LEFT JOIN keyword k ON rk.keyword_id = k.keyword_id WHERE r.review_status = 0 AND s.shop_id = #{shopId}
AND k.keyword IN
#{keyword}
GROUP BY r.review_id
ORDER BY r.review_date DESC
ORDER BY r.review_date ASC
ORDER BY r.review_date DESC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE shop
SET review_summary = #{reviewSummary}
WHERE shop_id = #{shopId}
